nta.declaration:
//CONSTANTS
const int N_PHASES = 4; 
const int N_SIGNALS = 20;
const int INT32_MAX = 2147483647; // up to 31 signal

const int MIN_GREEN = 4; 
const int YELLOW = 3;
const int HORIZON = 60;

//TYPES
typedef int[0, INT32_MAX] encoded_signal_t;
typedef int[0, N_SIGNALS-1] signal_t;
typedef int[0, N_PHASES-1] phase_id_t;

// bit binary patterns
const encoded_signal_t encoded_phases[phase_id_t] = {
787200,         //0: 11000-00000-11000-00000
1016800,        //1: 11111-00000-11111-00000
984000,         //2: 11110-00000-11110-00000
32800           //3: 00001-00000-00001-00000
};
/*
24600,          //4: 00000-11000-00000-11000
31775,          //5: 00000-11111-00000-11111
30750,          //6: 00000-11110-00000-11110
1025            //7: 00000-00001-00000-00001
};
*/
phase_id_t phase = 0;
phase_id_t next_phase = 0;

broadcast chan select_phase, phase_selected;
broadcast chan select_next[signal_t];

// ARRAYS
bool signal[signal_t];
int waiting[signal_t] = {
    10, 10, 10, 10,
    10, 10, 10, 10,
    10, 10, 10, 10,
    10, 10, 10, 10,
    10, 10, 10, 10
};

const int CYCLIST_SIGNALS = 4;
int cyclist_idx[CYCLIST_SIGNALS] = {0, 4, 8, 12};

// For more complicated transitions based on graph
const int options[N_PHASES] = {2, 3, 2, 3};

const int transitions[N_PHASES][N_PHASES] = {
    {0, 1, -1, -1},
    {1, 2, 3, -1},
    {2, 3, -1, -1},
    {3, 0, 1, -1}
};

// For self exclusive transitions definitions
const int options_excl[N_PHASES] = {1, 2, 1, 2};
const bool extension_allowed[N_PHASES] = {1, 1, 1, 1};
const int transitions_excl[N_PHASES][N_PHASES] = {
    {1, -1, -1, -1},
    {2, 3, -1, -1},
    {3, -1, -1, -1},
    {0, 1, -1, -1}
};

clock t;
hybrid clock C;
int total_waiting;

template.declaration:
void serve_users(int& users[signal_t]) {     
        for (i:signal_t)    {
            if (signal[i]) {   
                (users[i] - 1 < 0) ? 0 : users[i]--;
            }
    }
}

clock x;

void serve_users(int& users[signal_t]) {     
        for (i:signal_t)    {
            if (signal[i]) {   
                (users[i] - 1 < 0) ? 0 : users[i]--;
            }
    }
}

double r;

void queue_users(int& users[signal_t]) {     
    for (i:signal_t){
        users[i]++;
    }
}

void queue_users_random(int& users[signal_t]){
    for (i:signal_t){
        r = random(100.0); 
        if (r > 50){        
            users[i]++;
        }
    }
}

// Variables
clock x;

// Functions
void activate_signals(phase_id_t phase) {
   // Activate associated signals of encoded phase
   // Checks whether bits of s by i units is one
  encoded_signal_t s = encoded_phases[phase];  
  for (i:signal_t){
      signal[N_SIGNALS - 1 - i] = (s >> i) & 1;
  }
}

void red_all() {
  for (i:signal_t)    {
      signal[i] = false;
  }
}

void activate_transition_to(phase_id_t phase_to){
  encoded_signal_t s = encoded_phases[phase_to];
  bool phase_to_signal = false;
    for (i:signal_t){
      phase_to_signal = (s >> i) & 1;
      signal[N_SIGNALS - 1 - i] = signal[N_SIGNALS - 1 - i] & phase_to_signal;
  }
}

nta.system:
ctrl = Controller();
serving = Serving();
pm = PhaseSelectionGraph();
pm = PhaseSelectionGraphExcl();
q = Queueing();
c = Cost();
system ctrl, serving, pm, c;//, q;

query.formula:
strategy opt = minE (C) [<=HORIZON]{
next_phase, phase, 
waiting[0], waiting[1], waiting[2], waiting[3],
waiting[4], waiting[5], waiting[6], waiting[7],
waiting[8], waiting[9], waiting[10], waiting[11]
}->{t, ctrl.x}:<> t>=HORIZON
