// Variables
clock x;

// Functions
void activate_signals(phase_id_t phase) {
   // Activate associated signals of encoded phase
   // Checks whether bits of s by i units is one
  encoded_signal_t s = encoded_phases[phase];  
  for (i:signal_t){
      signal[N_SIGNALS - 1 - i] = (s >> i) & 1;
  }
}

void red_all() {
  for (i:signal_t)    {
      signal[i] = false;
  }
}

void activate_transition_to(phase_id_t phase_to){
  encoded_signal_t s = encoded_phases[phase_to];
  bool phase_to_signal = false;
    for (i:signal_t){
      phase_to_signal = (s >> i) & 1;
      signal[N_SIGNALS - 1 - i] = signal[N_SIGNALS - 1 - i] & phase_to_signal;
  }
}

void reset_transition_matrix(phase_id_t next_phase, int& requested_phase){
    // resets the variable transition matrix to its base
    if (next_phase == requested_phase){    
        transition_exists = _transition_exists;
        requested_phase = -1;
    }
}

void record_request(int& target) {
// if phase can serve request it must either do that or extend itself    
    if(target != -1){
        for(i:phase_id_t){
            if(transition_exists[i][target])
            {
                for(j:phase_id_t){
                    if(!transition_exists[j][target] && j != i && j != target){
                        transition_exists[i][j] = false;
                    }
                }       
            }
        }
    }
}

