void serve_users(int& users[signal_t]) {
    // remove users from the list if the corresponding index signal is true
    for (i:signal_t)    {
            if (signal[i]) {   
                (users[i] - 1 < 0) ? 0 : users[i]--;
            }
    }
}

nta.system:
// Declarations of SignalsWorking, SignalsNotWorking and SignalsNotWorking2 are identical

// Example one:
//     Simulate traffic signal phase choice and removal of waiting
//     Simulator works as expected, all queries work as expected
//
/*
main = SignalsWorking();
serving = Serving();
system main, serving;
*/

// Example two: 
//    Same purpose as example one
//    Simulator works as expected, queries without "under" work, the strategy query complains 
//    "State (main.ActivatePhase, serving.<some_id>) is time locked"
//

/*
main = SignalsNotWorking();
serving = Serving();
system main, serving;
*/

// Example three: same as example one but selection happens in another automata
//    PhaseManager is called through "select_phase!" and is responsible for updating "next_phase" integer
//    Simulator works as expected, but now all queries complain about "violated input determinism"  


main = SignalsNotWorking2();
serving = Serving();
pm = PhaseManagerCorrect();
system main, serving, pm;
